package javaProgPractice;

public class pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
				int row=5;
				int k=9;
		for(int i=5;i>=row;i--,k=9) {
			for(int j=5;j>=row-i;j--) {
		
			}
			while(k!=2*i-1) {
				System.out.print("* ");
				k--;
			}
			System.out.println();
		}
			}

		

	

}
