package javaprogram;

public class Firstpattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	/*	Write a java program to create a pyramid of numbers or any other symbols in pyramid patterns
 11111
22222
33333
44444
55555   */
	 
for(int i=1;i<=5;i++) {
	for(int j=1;j<=5;j++){
		System.out.print(i);
	}System.out.println();
}
	}

}
