package javaprogram;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	/*	Write a java program to create a pyramid of numbers or any other symbols in pyramid patterns.
		a. 1st pattern:
		12345
		12345
		12345
		12345
		12345    */
for(int i=1;i<=5;i++) {
	for(int j=1;j<=5;j++) {
		System.out.print(j);
		
	}
	System.out.println();
}
	}

}
